package com.kyumin.domaines.controller;import com.kyumin.domaines.service.ProductService;import lombok.RequiredArgsConstructor;import org.springframework.web.bind.annotation.*;import javax.validation.constraints.NotNull;@RequiredArgsConstructor@RequestMapping("/product")@RestControllerpublic class ProductController {    private final ProductService productService;    @PostMapping(value = "/{number}")    public void post(@PathVariable @NotNull Long number) {        productService.post(number);    }    @GetMapping    public void get() {        productService.get();    }}