package com.kyumin.domaines.service;import com.kyumin.domaines.domain.ProductIndex;import com.kyumin.domaines.repository.ProductEsRepository;import lombok.RequiredArgsConstructor;import lombok.extern.slf4j.Slf4j;import org.elasticsearch.common.geo.GeoPoint;import org.springframework.stereotype.Service;import javax.validation.constraints.NotNull;import java.time.LocalDateTime;@Service@Slf4j@RequiredArgsConstructorpublic class ProductService {    private final ProductEsRepository productEsRepository;    public void post(@NotNull Long i){        ProductIndex productIndex = productEsRepository.save(ProductIndex.builder().id(i).title("제목" + i).content("컨텐츠" + i).location(GeoPoint.fromGeohash(1243251365)).startDate(LocalDateTime.now()).build());        log.info("test : {}", productIndex);    }    public void get(){        Iterable<ProductIndex> all = productEsRepository.findAll();        all.forEach(productIndex -> log.info("test : {}", productIndex));    }}